import React, { useState } from 'react';
import './App.css';
import './index.css';

const API_URL = 'http://localhost:5001/api';

function App() {
  const [search, setSearch] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = (e) => setSearch(e.target.value);

  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_URL}/quotes/search?q=${encodeURIComponent(search)}`);
      if (!res.ok) throw new Error('Failed to fetch search results');
      const data = await res.json();
      setResults(data);
    } catch (err) {
      setError('Could not connect to the backend. Please make sure the backend server is running on port 5001.');
    }
    setLoading(false);
  };

  const handleClear = () => {
    setSearch('');
    setResults([]);
    setError(null);
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100">
      {/* Navbar */}
      <nav className="w-full bg-white/80 shadow-md py-4 px-6 flex items-center justify-between fixed top-0 left-0 z-10 backdrop-blur-md">
        <div className="flex items-center gap-2">
          <span className="text-2xl">üí°</span>
          <span className="text-xl font-bold text-indigo-700 tracking-wide">Quote Generator</span>
        </div>
        <button
          className="bg-gradient-to-r from-indigo-500 to-sky-400 text-white font-semibold px-4 py-2 rounded-lg shadow hover:from-indigo-600 hover:to-sky-500 transition-colors"
          onClick={handleClear}
        >
          Clear
        </button>
      </nav>
      {/* Spacer for navbar */}
      <div className="h-20" />
      {/* Hero Section */}
      <section className="flex flex-col items-center justify-center text-center mt-6 mb-4 px-4">
        <h1 className="text-4xl md:text-5xl font-extrabold text-indigo-700 mb-2 drop-shadow">Find Your Inspiration</h1>
        <p className="text-lg md:text-xl text-gray-600 mb-2">Search for motivational, life, love, and success quotes instantly.</p>
        <p className="text-base text-gray-400">Type a topic (e.g., <span className="font-semibold text-indigo-500">life</span>, <span className="font-semibold text-indigo-500">love</span>, <span className="font-semibold text-indigo-500">success</span>) and get inspired!</p>
      </section>
      {/* Main Card/Box */}
      <main className="flex flex-col items-center w-full flex-1">
        <div className="w-full max-w-xl bg-white/95 rounded-2xl shadow-2xl p-8 mt-2 mb-8">
          <form className="flex items-center gap-2 mb-6" onSubmit={handleSearchSubmit}>
            <input
              className="border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 w-full shadow-sm"
              type="text"
              placeholder="Enter a topic..."
              value={search}
              onChange={handleSearch}
            />
            <button className="bg-primary-600 hover:bg-primary-700 text-white font-semibold px-4 py-2 rounded-md transition-colors" type="submit">Search</button>
          </form>
          <div className="mt-6">
            {loading ? (
              <div className="text-center text-gray-500">Loading...</div>
            ) : error ? (
              <div className="text-center text-red-600">{error}</div>
            ) : results.length === 0 && search ? (
              <div className="text-center text-gray-500">No quotes found.</div>
            ) : (
              <section>
                <div className="flex items-center gap-2 mb-4">
                  <span className="text-2xl">üìú</span>
                  <h2 className="text-xl font-bold text-indigo-600">Quotes</h2>
                </div>
                <div className="grid grid-cols-1 gap-6">
                  {results.map((q, i) => (
                    <div
                      className="bg-white rounded-lg shadow p-6 flex flex-col gap-2 border border-gray-200 quote-card"
                      key={q.id}
                    >
                      <p className="text-lg font-medium mb-2">"{q.text}"</p>
                      <p className="text-gray-700 mb-1">- {q.author}</p>
                      {q.topic && <span className="text-xs bg-gray-100 rounded px-2 py-1 text-gray-600">#{q.topic}</span>}
                      <div className="text-sm text-pink-600">‚ù§Ô∏è {q.likes}</div>
                    </div>
                  ))}
                </div>
              </section>
            )}
          </div>
        </div>
      </main>
      {/* Footer */}
      <footer className="w-full text-center py-4 text-gray-400 text-sm mt-auto">
        Made with <span className="text-pink-500">‚ô•</span> by Your Name &copy; {new Date().getFullYear()}
      </footer>
    </div>
  );
}

export default App;
