import React, { useState, useEffect } from 'react';
import './App.css';

const API_URL = 'http://localhost:5000/api';

function App() {
  const [quotes, setQuotes] = useState([]);
  const [search, setSearch] = useState('');
  const [filteredQuotes, setFilteredQuotes] = useState([]);
  const [randomQuote, setRandomQuote] = useState(null);
  const [form, setForm] = useState({ text: '', author: '', topic: '' });
  const [loading, setLoading] = useState(true);

  // Fetch all quotes
  const fetchQuotes = async () => {
    setLoading(true);
    const res = await fetch(`${API_URL}/quotes`);
    const data = await res.json();
    setQuotes(data);
    setLoading(false);
  };

  // Fetch a random quote
  const fetchRandomQuote = async (list = null) => {
    if (list && list.length > 0) {
      setRandomQuote(list[Math.floor(Math.random() * list.length)]);
      return;
    }
    const res = await fetch(`${API_URL}/quote`);
    const data = await res.json();
    setRandomQuote(data);
  };

  useEffect(() => {
    fetchQuotes();
  }, []);

  useEffect(() => {
    let filtered = quotes.filter(q =>
      q.text.toLowerCase().includes(search.toLowerCase()) ||
      q.author.toLowerCase().includes(search.toLowerCase()) ||
      (q.topic && q.topic.toLowerCase().includes(search.toLowerCase()))
    );
    setFilteredQuotes(filtered.length ? filtered : quotes);
    if (filtered.length) {
      setRandomQuote(filtered[Math.floor(Math.random() * filtered.length)]);
    } else {
      setRandomQuote(null);
    }
  }, [search, quotes]);

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleNewQuote = () => {
    if (filteredQuotes.length > 0) {
      let newQuote;
      do {
        newQuote = filteredQuotes[Math.floor(Math.random() * filteredQuotes.length)];
      } while (filteredQuotes.length > 1 && newQuote.id === randomQuote.id);
      setRandomQuote(newQuote);
    }
  };

  const handleLike = async (id) => {
    await fetch(`${API_URL}/quotes/${id}/like`, { method: 'POST' });
    fetchQuotes();
  };

  const handleDelete = async (id) => {
    await fetch(`${API_URL}/quotes/${id}`, { method: 'DELETE' });
    fetchQuotes();
  };

  const handleFormChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!form.text.trim() || !form.author.trim()) return;
    await fetch(`${API_URL}/quotes`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });
    setForm({ text: '', author: '', topic: '' });
    fetchQuotes();
  };

  return (
    <div className="quote-container">
      <div className="quote-box enhanced">
        <h1 className="app-title">Quote Generator</h1>
        <input
          className="search-bar"
          type="text"
          placeholder="Search by topic, author, or text..."
          value={search}
          onChange={handleSearch}
        />
        {loading ? (
          <div className="loading">Loading...</div>
        ) : randomQuote ? (
          <div className="quote-content">
            <p className="quote-text">"{randomQuote.text}"</p>
            <p className="quote-author">- {randomQuote.author}</p>
            {randomQuote.topic && <span className="quote-topic">#{randomQuote.topic}</span>}
            <div className="quote-actions">
              <button className="like-btn" onClick={() => handleLike(randomQuote.id)}>
                ‚ù§Ô∏è {randomQuote.likes}
              </button>
              <button className="delete-btn" onClick={() => handleDelete(randomQuote.id)}>
                üóëÔ∏è
              </button>
            </div>
          </div>
        ) : (
          <div className="no-quotes">No quotes found.</div>
        )}
        <button className="new-quote-btn" onClick={handleNewQuote} disabled={filteredQuotes.length < 2}>
          New Quote
        </button>
        <form className="add-quote-form" onSubmit={handleFormSubmit}>
          <h2>Add a Quote</h2>
          <input
            type="text"
            name="text"
            placeholder="Quote text"
            value={form.text}
            onChange={handleFormChange}
            required
          />
          <input
            type="text"
            name="author"
            placeholder="Author"
            value={form.author}
            onChange={handleFormChange}
            required
          />
          <input
            type="text"
            name="topic"
            placeholder="Topic (optional)"
            value={form.topic}
            onChange={handleFormChange}
          />
          <button type="submit">Add Quote</button>
        </form>
      </div>
    </div>
  );
}

export default App;
