import React, { useState } from 'react';
import './App.css';
import './index.css';

const API_URL = 'http://localhost:5001/api';

function App() {
  const [search, setSearch] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = (e) => setSearch(e.target.value);

  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_URL}/quotes/search?q=${encodeURIComponent(search)}`);
      if (!res.ok) throw new Error('Failed to fetch search results');
      const data = await res.json();
      setResults(data);
    } catch (err) {
      setError('Could not connect to the backend. Please make sure the backend server is running on port 5001.');
    }
    setLoading(false);
  };

  const handleClear = () => {
    setSearch('');
    setResults([]);
    setError(null);
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-indigo-100 via-sky-100 to-purple-100">
      {/* Navbar */}
      <nav className="w-full bg-white/80 shadow-md py-4 px-6 flex items-center justify-between fixed top-0 left-0 z-10 backdrop-blur-md">
        <div className="flex items-center gap-2 flex-1">
          <span className="text-2xl">💡</span>
          <span className="app-title-gradient">Quote Generator</span>
        </div>
        <div className="flex-1 flex justify-end">
          <button
            className="clear-btn"
            onClick={handleClear}
          >
            <span role="img" aria-label="reset" style={{marginRight: '0.5rem'}}>🔄</span>Reset
          </button>
        </div>
      </nav>
      {/* Spacer for navbar */}
      <div className="h-20" />
      {/* Hero Section */}
      <section className="hero-section">
        <h1>Find Your Inspiration</h1>
        <p>Search for motivational, life, love, and success quotes instantly.</p>
        <p>Type a topic (e.g., <span className="font-semibold text-indigo-500">life</span>, <span className="font-semibold text-indigo-500">love</span>, <span className="font-semibold text-indigo-500">success</span>) and get inspired!</p>
      </section>
      {/* Main Card/Box */}
      <main className="flex flex-col items-center w-full flex-1">
        <div className="main-card">
        <form className="search-bar-form" onSubmit={handleSearchSubmit}>
          <input
            type="text"
              placeholder="Enter a topic..."
            value={search}
            onChange={handleSearch}
          />
          <button className="search-btn" type="submit">Search</button>
        </form>
          <div className="results-section">
        {loading ? (
              <div className="text-center text-gray-500">Loading...</div>
            ) : error ? (
              <div className="text-center text-red-600">{error}</div>
            ) : results.length === 0 && search ? (
              <div className="text-center text-gray-500">No quotes found.</div>
            ) : (
              <section>
                <div className="results-header">
                  <span className="text-2xl">📜</span>
                  <h2>Quotes</h2>
                </div>
                <div className="grid grid-cols-1 gap-6">
                  {results.map((q, i) => (
                    <div
                      className="quote-card"
                      key={q.id}
                    >
                      <p className="quote-text">"{q.text}"</p>
                      <p className="quote-author">- {q.author}</p>
                      {q.topic && <span className="quote-topic">#{q.topic}</span>}
                      <div className="quote-likes">❤️ {q.likes}</div>
                    </div>
                  ))}
            </div>
              </section>
            )}
          </div>
      </div>
      </main>
      {/* Footer */}
      <footer>
        Made with <span className="text-pink-500">♥</span> by Kashaf &copy; {new Date().getFullYear()}
      </footer>
    </div>
  );
}

export default App;
