import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import './App.css';
import AllQuotes from './AllQuotes';
import SearchResults from './SearchResults';
import 'tailwindcss/tailwind.css';

const API_URL = 'http://localhost:5001/api';

function Home({ quotes, fetchQuotes, loading, handleLike, handleDelete, handleFormSubmit, form, handleFormChange }) {
  const [search, setSearch] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => setSearch(e.target.value);
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (search.trim()) navigate(`/search?q=${encodeURIComponent(search)}`);
  };

  // Pick a random quote from all quotes
  const [randomQuote, setRandomQuote] = useState(null);
  useEffect(() => {
    if (quotes.length) setRandomQuote(quotes[Math.floor(Math.random() * quotes.length)]);
  }, [quotes]);

  const handleNewQuote = () => {
    if (quotes.length > 0) {
      let newQuote;
      do {
        newQuote = quotes[Math.floor(Math.random() * quotes.length)];
      } while (quotes.length > 1 && newQuote.id === randomQuote.id);
      setRandomQuote(newQuote);
    }
  };

  return (
    <div className="quote-container animated-bg">
      <div className="quote-box enhanced colorful">
        <form className="search-bar-form flex items-center gap-2 mb-4" onSubmit={handleSearchSubmit}>
          <input
            className="search-bar top border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500 w-full shadow-sm"
            type="text"
            placeholder="Enter a topic..."
            value={search}
            onChange={handleSearch}
          />
          <button className="search-btn bg-primary-600 hover:bg-primary-700 text-white font-semibold px-4 py-2 rounded-md transition-colors" type="submit">Search</button>
        </form>
        {loading ? (
          <div className="loading">Loading...</div>
        ) : randomQuote ? (
          <div className="quote-content">
            <p className="quote-text">"{randomQuote.text}"</p>
            <p className="quote-author">- {randomQuote.author}</p>
            {randomQuote.topic && <span className="quote-topic">#{randomQuote.topic}</span>}
            <div className="quote-actions">
              <button className="like-btn" onClick={() => handleLike(randomQuote.id)}>
                ❤️ {randomQuote.likes}
              </button>
              <button className="delete-btn" onClick={() => handleDelete(randomQuote.id)}>
                🗑️
              </button>
            </div>
          </div>
        ) : (
          <div className="no-quotes">No quotes found.</div>
        )}
        <button className="new-quote-btn" onClick={handleNewQuote} disabled={quotes.length < 2}>
          New Quote
        </button>
        <form className="add-quote-form" onSubmit={handleFormSubmit}>
          <h2>Add a Quote</h2>
          <input
            type="text"
            name="text"
            placeholder="Quote text"
            value={form.text}
            onChange={handleFormChange}
            required
          />
          <input
            type="text"
            name="author"
            placeholder="Author"
            value={form.author}
            onChange={handleFormChange}
            required
          />
          <input
            type="text"
            name="topic"
            placeholder="Topic (optional)"
            value={form.topic}
            onChange={handleFormChange}
          />
          <button type="submit">Add Quote</button>
        </form>
      </div>
    </div>
  );
}

function App() {
  const [quotes, setQuotes] = useState([]);
  const [form, setForm] = useState({ text: '', author: '', topic: '' });
  const [loading, setLoading] = useState(true);

  // Fetch all quotes
  const fetchQuotes = async () => {
    setLoading(true);
    const res = await fetch(`${API_URL}/quotes`);
    const data = await res.json();
    setQuotes(data);
    setLoading(false);
  };

  useEffect(() => {
    fetchQuotes();
  }, []);

  const handleLike = async (id) => {
    await fetch(`${API_URL}/quotes/${id}/like`, { method: 'POST' });
    fetchQuotes();
  };

  const handleDelete = async (id) => {
    await fetch(`${API_URL}/quotes/${id}`, { method: 'DELETE' });
    fetchQuotes();
  };

  const handleFormChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (!form.text.trim() || !form.author.trim()) return;
    await fetch(`${API_URL}/quotes`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });
    setForm({ text: '', author: '', topic: '' });
    fetchQuotes();
  };

  return (
    <Router>
      <div className="main-navbar">
        <Link to="/" className="nav-title">Quote Generator</Link>
        <Link to="/all" className="nav-link">All Quotes</Link>
      </div>
      <div className="app-content">
        <Routes>
          <Route path="/all" element={<AllQuotes quotes={quotes} />} />
          <Route path="/search" element={<SearchResults />} />
          <Route path="/" element={
            <Home
              quotes={quotes}
              fetchQuotes={fetchQuotes}
              loading={loading}
              handleLike={handleLike}
              handleDelete={handleDelete}
              handleFormSubmit={handleFormSubmit}
              form={form}
              handleFormChange={handleFormChange}
            />
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
