import React, { useState } from 'react';
import './App.css';

const quotes = [
  { text: "The best way to get started is to quit talking and begin doing.", author: "Walt Disney", topic: "motivation" },
  { text: "Don't let yesterday take up too much of today.", author: "Will Rogers", topic: "inspiration" },
  { text: "It's not whether you get knocked down, it's whether you get up.", author: "Vince Lombardi", topic: "perseverance" },
  { text: "If you are working on something exciting, it will keep you motivated.", author: "Steve Jobs", topic: "motivation" },
  { text: "Success is not in what you have, but who you are.", author: "Bo Bennett", topic: "success" }
];

function getRandomQuote(filteredQuotes) {
  return filteredQuotes[Math.floor(Math.random() * filteredQuotes.length)];
}

function App() {
  const [search, setSearch] = useState('');
  const [filteredQuotes, setFilteredQuotes] = useState(quotes);
  const [quote, setQuote] = useState(getRandomQuote(quotes));

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearch(value);
    const filtered = quotes.filter(q =>
      q.text.toLowerCase().includes(value.toLowerCase()) ||
      q.author.toLowerCase().includes(value.toLowerCase()) ||
      (q.topic && q.topic.toLowerCase().includes(value.toLowerCase()))
    );
    setFilteredQuotes(filtered.length ? filtered : quotes);
    setQuote(getRandomQuote(filtered.length ? filtered : quotes));
  };

  const handleNewQuote = () => {
    let newQuote;
    do {
      newQuote = getRandomQuote(filteredQuotes);
    } while (filteredQuotes.length > 1 && newQuote.text === quote.text);
    setQuote(newQuote);
  };

  return (
    <div className="quote-container">
      <div className="quote-box enhanced">
        <h1 className="app-title">Quote Generator</h1>
        <input
          className="search-bar"
          type="text"
          placeholder="Search by topic, author, or text..."
          value={search}
          onChange={handleSearch}
        />
        <div className="quote-content">
          <p className="quote-text">"{quote.text}"</p>
          <p className="quote-author">- {quote.author}</p>
          {quote.topic && <span className="quote-topic">#{quote.topic}</span>}
        </div>
        <button className="new-quote-btn" onClick={handleNewQuote}>New Quote</button>
      </div>
    </div>
  );
}

export default App;
