import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import './AllQuotes.css';

const COLORS = [
  '#fbbf24', '#34d399', '#60a5fa', '#f472b6', '#a78bfa', '#f87171', '#38bdf8', '#facc15', '#4ade80', '#c084fc'
];

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

function SearchResults() {
  const query = useQuery().get('q') || '';
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);
    fetch(`http://localhost:5001/api/quotes/search?q=${encodeURIComponent(query)}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch search results');
        return res.json();
      })
      .then(data => {
        setResults(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Could not connect to the backend. Please make sure the backend server is running on port 5001.');
        setLoading(false);
      });
  }, [query]);

  return (
    <div className="all-quotes-page">
      <h2 className="all-quotes-title">Search Results for "{query}"</h2>
      <Link to="/" className="nav-link" style={{marginBottom: '1.5rem', display: 'inline-block'}}>Back to Home</Link>
      <div className="quotes-list">
        {loading ? (
          <div className="loading">Loading...</div>
        ) : error ? (
          <div className="no-quotes" style={{color: 'red'}}>{error}</div>
        ) : results.length === 0 ? (
          <div className="no-quotes">No quotes found.</div>
        ) : (
          results.map((q, i) => (
            <div
              className="quote-card"
              key={q.id}
              style={{ background: COLORS[i % COLORS.length] }}
            >
              <p className="quote-text">"{q.text}"</p>
              <p className="quote-author">- {q.author}</p>
              {q.topic && <span className="quote-topic">#{q.topic}</span>}
              <div className="quote-likes">❤️ {q.likes}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default SearchResults;
